import React, { useEffect } from 'react';
import { useQuery, gql } from '@apollo/client';
import NoteFeed from '../components/NoteFeed';


const GET_MY_NOTES = gql`
query me {
    me {
        id
        username
    notes {
        id
        createdAt
        content
        favoriteCount
        author {
                username
                id
                avatar
            }
        }
    }
}
`;

const GET_NOTES = gql`
query noteFeed($cursor: String) {
noteFeed(cursor: $cursor) {
cursor
hasNextPage
notes {
id
createdAt
content
favoriteCount
author {
username
id
avatar
}
}
}
}
`;

const GET_NOTE = gql`
query note($id: ID!) {
note(id: $id) {
id
createdAt
content
favoriteCount
author {
username
id
avatar
}
}
}
`;

const IS_LOGGED_IN = gql`
{
isLoggedIn @client
}
`;

const GET_MY_FAVORITES = gql`
query me {
me {
id
username
favorites {
id
createdAt
content
favoriteCount
author {
username
id
avatar
}
}
}
}
`;

const GET_ME = gql`
query me {
me {
id
favorites {
id
}
}
}
`;


export {
    GET_NOTES,
    GET_NOTE,
    GET_MY_NOTES,
    GET_MY_FAVORITES,
    GET_ME,
    IS_LOGGED_IN
};
